pattern:
// dnb pattern in C at 174 BPM
setcpm(174)

stack(
  // Drums
  s("bd ~ ~ [bd bd] ~ ~ bd ~, ~ ~ cp ~ [~ cp] ~ cp ~ ~, hh*16").fast(2),

  // Bass
  note("C1 ~ ~ C2 ~ C1 ~ ~").s("square").cutoff(400),

  // Chords
  note("C" "G" "Am" "F").s("sawtooth").struct("1 ~ 1 ~").release(0.1).gain(0.6),

  // Melody
  note("g#4 a#4 g#3 d3 c3 d4 f5 g5").s("triangle").struct("~ 1 ~ 1 1 ~ 1 ~").delay(0.25).room(0.3).gain(0.5)
).gain(0.8)
