pattern:
// dnb pattern in F# at 176 BPM
setcpm(176)

stack(
  // Drums
  s("bd ~ ~ [bd bd] ~ ~ bd ~, ~ ~ cp ~ [~ cp] ~ cp ~ ~, hh*16").fast(2),

  // Bass
  note("F#1 ~ ~ F#2 ~ F#1 ~ ~").s("square").cutoff(400),

  // Chords
  note("F#" "C#" "D#m" "B").s("sawtooth").struct("1 ~ 1 ~").release(0.1).gain(0.6),

  // Melody
  note("f#3 f#5 f#4 g#3 g#4 f#3 d5 c#5").s("triangle").struct("~ 1 ~ 1 1 ~ 1 ~").delay(0.25).room(0.3).gain(0.5)
).gain(0.8)
