// house pattern in D at 122 BPM
setcpm(122)

stack(
  // Drums
  s("bd*4, [~ hh]*4, ~ cp ~ cp, oh ~ oh ~"),

  // Bass
  note("D2 ~ D2 ~").s("sine").gain(0.8),

  // Chords
  note("<D A Bm G>").s("sawtooth").struct("1 ~ 1 ~").release(0.1).gain(0.6),

  // Melody
  note("f5 g3 f3 g5 f3 g5 a4 a#5").s("triangle").struct("~ 1 ~ 1 1 ~ 1 ~").delay(0.25).room(0.3).gain(0.5)
).gain(0.8)
