pattern:
// house pattern in A at 126 BPM
setcpm(126)

stack(
  // Drums
  s("bd*4, [~ hh]*4, ~ cp ~ cp, oh ~ oh ~"),

  // Bass
  note("A2 ~ A2 ~").s("sine").gain(0.8),

  // Chords
  note("<A E F#m D>").s("sawtooth").struct("1 ~ 1 ~").release(0.1).gain(0.6),

  // Melody
  note("a3 g4 g4 f5 b5 a5 f4 g5").s("triangle").struct("~ 1 ~ 1 1 ~ 1 ~").delay(0.25).room(0.3).gain(0.5)
).gain(0.8)
