setcpm(170)

stack(
  // Drums
  s("bd ~ [~ bd] bd ~ [bd bd] bd ~, ~ cp ~ [~ cp] cp ~, hh*32").fast(2),

  // Bass
  note("D2 D2 D2 D2").s("sawtooth").cutoff(800),

  // Chords
  note("<D A Bm G>").s("sawtooth").struct("1 ~ 1 ~").release(0.1).gain(0.6),

  // Melody
  note("c3 a#3 d3 a3 a#4 a4 f5 e3").s("triangle").struct("~ 1 ~ 1 1 ~ 1 ~").delay(0.25).room(0.3).gain(0.5)
).gain(0.8)
