pattern:
// Nu-Jazz Electronic Session - 64 cycles at 95 BPM
// Structure: Intro(8) -> Theme A(16) -> Theme B(16) -> Solo(16) -> Theme A(8) -> Outro(8)
setcpm(95)

// Section timing: using slowcat for 64-cycle arrangement
const section = (intro, themeA, themeB, solo, repriseA, outro) =>
  slowcat(
    // Intro: 8 cycles
    intro.slow(8),
    // Theme A: 16 cycles  
    themeA.slow(16),
    // Theme B: 16 cycles
    themeB.slow(16),
    // Solo: 16 cycles
    solo.slow(16),
    // Theme A reprise: 8 cycles
    repriseA.slow(8),
    // Outro: 8 cycles
    outro.slow(8)
  )

// Jazz progressions (Dm7 - G13 - Cmaj9 - A7alt turnaround)
const chordsIntro = note("[d3,f3,a3,c4] ~ ~ ~").s("piano").velocity(0.4).room(0.5).delay(0.2)
const chordsA = note("[d3,f3,a3,c4] [g2,b3,d4,f4,e4] [c3,e3,g3,b3,d4] [a2,c#3,e3,g3]").s("piano").velocity(0.6).room(0.4).delay(0.15)
const chordsB = note("[f3,a3,c4,e4] [bb2,d3,f3,a3,c4] [eb3,g3,bb3,d4] [a2,c#3,g3,b3]").s("piano").velocity(0.55).room(0.45).delay(0.18)

// Walking bass - Dorian mode foundation
const bassIntro = note("d2 ~ ~ ~").s("sawtooth").lpf(400).lpq(2).gain(0.5).room(0.2)
const bassA = note("d2 e2 f2 g2 a2 b2 c3 a2").s("sawtooth").lpf(500).lpq(3).gain(0.65).room(0.2)
const bassB = note("f2 g2 a2 bb2 c3 d3 eb3 c3").s("sawtooth").lpf(450).lpq(2.5).gain(0.6).room(0.25)
const bassSolo = note("d2 f2 a2 c3 e2 g2 b2 d3").s("sawtooth").lpf(550).lpq(3).gain(0.6).room(0.2)

// Brushed electronic drums - swing feel via off-grid placements
const drumsIntro = stack(
  s("~ ~ ~ hh:2").gain(0.25).room(0.6),
  s("~ ~ ~ ~").gain(0)
)
const drumsA = stack(
  s("bd ~ ~ bd:1 ~ ~ bd ~ ").gain(0.55).lpf(200),
  s("~ [~ cp:1] ~ [~ cp:1]").gain(0.35).hpf(800).room(0.5).delay(0.1),
  s("hh:2*3 [hh:1 hh:2] hh:2*3 [hh:1 hh:3]").gain(0.3).pan(sine.range(0.3,0.7)).hpf(6000)
).swing(0.2)
const drumsB = stack(
  s("bd ~ bd:1 ~ bd ~ bd:1 ~").gain(0.5).lpf(180),
  s("~ cp:2 ~ [~ cp:1]").gain(0.32).hpf(900).room(0.55).delay(0.12),
  s("[hh:1 hh:2 hh:1] hh:3 [hh:2 hh:1 hh:2] hh:3").gain(0.28).pan(sine.range(0.35,0.65)).hpf(5500)
).swing(0.18)
const drumsSolo = stack(
  s("bd ~ ~ ~ bd:1 ~ ~ ~").gain(0.45).lpf(160),
  s("~ ~ cp:1 ~ ~ ~ cp:2 ~").gain(0.3).hpf(850).room(0.6).delay(0.15),
  s("hh:2*4").gain(0.35).pan(rand.range(0.2,0.8)).hpf(5000)
).swing(0.15)
const drumsOutro = stack(
  s("bd ~ ~ ~").gain(0.3).lpf(150),
  s("~ ~ ~ hh:2").gain(0.2).room(0.7)
)

// Melodic lines - Dorian/Mixolydian scales
const melodyIntro = note("~ ~ ~ ~").s("triangle").gain(0)
const melodyA = note("d5 [e5 f5] g5 ~ a5 [g5 f5] e5 d5").s("triangle").lpf(2000).room(0.5).delay(0.25).gain(0.4)
const melodyB = note("f5 [g5 a5] bb5 ~ c6 [bb5 a5] g5 f5").s("triangle").lpf(2200).room(0.55).delay(0.28).gain(0.38)
const melodySolo = note("d5 f5 a5 c6 e5 g5 b5 d6 c6 a5 f5 d5 e5 g5 f5 e5").s("triangle").lpf(2500).room(0.6).delay(0.3).gain(0.45).fast(2)
const melodyOutro = note("d5 ~ ~ a4 ~ ~ d4 ~").s("triangle").lpf(1500).room(0.7).delay(0.35).gain(0.3)

// Electronic texture layer - subtle pads
const padIntro = note("[d3,a3]").s("sine").lpf(800).room(0.8).gain(0.15).slow(2)
const padA = note("[d3,f3,a3] [g3,b3,d4]").s("sine").lpf(1000).room(0.7).gain(0.2).slow(2)
const padB = note("[f3,a3,c4] [bb3,d4,f4]").s("sine").lpf(900).room(0.75).gain(0.18).slow(2)
const padSolo = note("[d3,a3,e4]").s("sine").lpf(1200).room(0.8).gain(0.22).slow(4)
const padOutro = note("[d3,a3]").s("sine").lpf(600).room(0.9).gain(0.12).slow(4)

// Final stack with all layers sectioned
stack(
  section(chordsIntro, chordsA, chordsB, chordsA, chordsA, chordsIntro),
  section(bassIntro, bassA, bassB, bassSolo, bassA, bassIntro),
  section(drumsIntro, drumsA, drumsB, drumsSolo, drumsA, drumsOutro),
  section(melodyIntro, melodyA, melodyB, melodySolo, melodyA, melodyOutro),
  section(padIntro, padA, padB, padSolo, padA, padOutro)
).gain(0.75)
