pattern:
// trap pattern in B at 135 BPM
setcpm(135)

stack(
  // Drums
  s("bd [bd bd] ~ bd, ~ cp ~ cp, hh*16").swing(0.2),

  // Bass
  note("B2 B2 B2 B2").s("sawtooth").cutoff(800),

  // Chords
  note("<B F# G#m E>").s("sawtooth").struct("1 ~ 1 ~").release(0.1).gain(0.6),

  // Melody
  note("c#5 b3 a4 d3 a4 a5 g4 g5").s("triangle").struct("~ 1 ~ 1 1 ~ 1 ~").delay(0.25).room(0.3).gain(0.5)
).gain(0.8)
