pattern:
// trap pattern in F# at 140 BPM
setcpm(140)

stack(
  // Drums
  s("bd [bd bd] ~ bd, ~ cp ~ cp, hh*16").swing(0.2),

  // Bass
  note("F#2 F#2 F#2 F#2").s("sawtooth").cutoff(800),

  // Chords
  note("F#" "C#" "D#m" "B").s("sawtooth").struct("1 ~ 1 ~").release(0.1).gain(0.6),

  // Melody
  note("e3 d4 a5 b4 e3 f#5 e5 a3").s("triangle").struct("~ 1 ~ 1 1 ~ 1 ~").delay(0.25).room(0.3).gain(0.5)
).gain(0.8)
